import React, { Component } from "react"

const postItems = [
  {
    id: 1,
    title: "Post 1",
    text: "Some text 1",
    created_date: '17/10/2020'
  },

  {
    id: 2,
    title: "Post 2",
    text: "Some text 2",
    created_date: '17/10/2020'
  },

  {
    id: 3,
    title: "Post 3",
    text: "Some text 3",
    created_date: '17/10/2020'
  },
];

class App extends Component {
    constructor(props) {
      super(props);
      this.state = { postItems };
    };

    render() {
      return (
        <main className="content">
            <div className="row">
              <div className="col-md-6 col-sm-10 mx-auto p-0">
                <div className="card p-3">
                  <ul className="list-group list-group-flush">
                  {
                    this.state.postItems.map(item => (
                      <div>
                        <h1>{item.title}</h1>
                        <span>{item.description}</span>
                      </div>
                    ))
                  }
                  </ul>
                </div>
              </div>
            </div>
        </main>
      )
    }
 }

export default App;
